mapper.select:
select er.equipment_iedger_id, er.equipment_name, er.region_id, er.project_id, er.equipment_type_id, po.project_name as region_name, po2.project_name as project_name, et.equipment_type_name as equipment_type_name, er.installation_address, er.manufacturer, er.manufacturer_address, er.contact_number, er.unit_model, er.loop_number, er.factory_number, er.temperature_restriction, er.power, er.capacity, er.voltage, er.electric_current, er.weight, er.lift, er.speed, er.flow, er.remarks, er.if_easy_use, er.if_intact, er.using_state, er.intact_state, er.installation_unit, er.install_contacts, er.contact_information, er.warranty_termination, er.install_time, er.enable_time, er.handover_time_type, er.create_time, er.update_time from equipment_iedger er left join equipment_type et on er.equipment_type_id = et.equipment_type_id join power_project po on er.region_id = po.project_id join power_project po2 on er.project_id = po2.project_id
er.region_id = #{regionId}
and er.equipment_type_id = #{equipmentTypeId}
and er.equipment_name LIKE concat('%',#{equipmentName},'%')
order by er.create_time desc

select er.equipment_iedger_id, er.equipment_name, er.region_id, er.project_id, er.equipment_type_id, po.project_name as region_name, po2.project_name as project_name,
er.installation_address, er.manufacturer, er.manufacturer_address, er.contact_number, er.unit_model, er.loop_number, er.factory_number, er.temperature_restriction, er.power, er.capacity, er.voltage, er.electric_current, er.weight, er.lift, er.speed, er.flow, er.remarks, er.if_easy_use, er.if_intact, er.using_state, er.intact_state, er.installation_unit, er.install_contacts, er.contact_information, er.warranty_termination, er.install_time, er.enable_time, er.handover_time_type, er.create_time, er.update_time from equipment_iedger er
join power_project po on er.region_id = po.project_id join power_project po2 on er.project_id = po2.project_id where er.equipment_iedger_id=#{equipmentIedgerId}
